mapper.select:
select users.user_id as userId, users.user_name as name, users.login_name as loginName, ui.user_sex as sex, '' as bithYear, '' as photoUrl, users.create_time as createTime, uf.user_status as userStatus from users as invationUser inner join user_froms uf on uf.invitation_code=invationUser.invitation_code inner join users on users.user_id=uf.user_id left join user_infos ui on ui.user_id =users.user_id left join appointment_orders as ao on users.user_id=ao.create_user_id where invationUser.user_id=#{userId}
and uf.user_status=#{userStatus}
and uf.user_status=1 and ao.order_status=3

select count(1) as nums from users as invationUser inner join user_froms uf on uf.invitation_code=invationUser.invitation_code inner join users on users.user_id=uf.user_id left join user_infos ui on ui.user_id =users.user_id left join appointment_orders as ao on users.user_id=ao.create_user_id where invationUser.user_id=#{userId}
and uf.user_status=#{userStatus}
and uf.user_status=1 and ao.order_status=3
